	public static String[] makeList(String listText, int listNumber) {
		String returnArray[] = new String[listNumber];
		
		for (int i = 0; i < listNumber; i++) {
			int subStart = 0;
			int subEnd;
			int listIndex = 0;
			for (int j = 0; j < listText.length(); j++) {
				if (listText.substring(j, j+1).equals("\n")) {
					subEnd = j;
					returnArray[listIndex] = listText.substring(subStart, subEnd);
					subStart = j + 1;
					listIndex++;
				}
			}
		}
		
		return returnArray;
	}
	
	public static String roll(String rollList) {
		int rollResult = 0;
		String result = "";
		if (rollList.equals("ChaosList")) {
			rollResult = (int) Math.floor(Math.random() * 200);
			result = chaosList[rollResult];
		} else if (rollList.equals("EnchantWorldLand")) {
			rollResult = (int) Math.floor(Math.random() * 20);
			result = enchantWorldLand[rollResult];
		} else if (rollList.equals("PersonaLand")) {
			rollResult = (int) Math.floor(Math.random() * 20);
			result = personaLand[rollResult];
		} else if (rollList.equals("WackyLand")) {
			rollResult = (int) Math.floor(Math.random() * 10) + (int) Math.floor(Math.random() * 10);
			result = wackyLand[rollResult];
			rollResult++;
		} else if (rollList.equals("1d3")) {
			rollResult = (int) Math.floor(Math.random() * 3);
		} else if (rollList.equals("1d6")) {
			rollResult = (int) Math.floor(Math.random() * 6);
		} else if (rollList.equals("1d10")) {
			rollResult = (int) Math.floor(Math.random() * 10);
		} else if (rollList.equals("Coin")) {
			rollResult = (int) Math.floor(Math.random() * 2);
			if (rollResult < 1) {
				result = "Heads.";
			} else {
				result = "Tails.";
			}
		}
		
		if (rollList.equals("Coin")) {
			return result;
		} else {
			return (rollResult + 1) + ". " + result;
		}
	}
